INSERT INTO `equipements` (`id`,`id_item`, `id_image`, `nom`, `description`, `lvl`, `categorie`, `poids`, `liste_ingredient`, `metier`, `lvl_metier`, `dans_craft`, `drop_monstres`,`effets`, `critere`, `panoplie`) VALUES (1,70,1001,'Amulette du Hibou','Cette amulette augmente l''intelligence de son porteur.',8,'Amulette',4,'1xHuile de Sésame + 5xPince du Crabe','Bijoutier',8,null,null,'+5 à 7 Intelligence & +2 Dommages Feu',null,null),(2,77,1005,'Amulette de l''Ours','Cette amulette augmente la force de son porteur.',8,'Amulette',10,'10xBave de Bouftou + 5xFeu Intérieur','Bijoutier',8,null,null,'+5 à 7 Force & +2 Dommages Terre',null,null),(3,84,1006,'Amulette du Loup','Cette amulette augmente la vitalité de son porteur.',8,'Amulette',4,'1xFerrite + 5xRelique d''Incarnam','Bijoutier',8,null,null,'+16 à 20 Vitalité & +3 à 4 Soins',null,null),(4,103,9007,'Anneau de Sagesse','Cet anneau renforce la sagesse de son porteur.',9,'Anneau',10,'5xRelique d''Incarnam + 5xFeu Intérieur','Bijoutier',9,'Bâton de fausse magie, Cape Hôte',null,'+4 à 6 Sagesse',null,null),(5,112,9009,'Anneau de Chance','Cet anneau renforce la chance de son porteur.',9,'Anneau',10,'1xFerrite + 7xAiles de Moskito','Bijoutier',9,'Pelle à Thart''',null,'+7 à 10 Chance & +1 Dommages Eau',null,null),(6,121,9001,'Anneau d''Agilité','Cet anneau augmente l''agilité de son porteur.',9,'Anneau',10,'4xNageoire de Compétition + 1xFerrite','Bijoutier',9,null,null,'+7 à 10 Agilité & +1 Dommages Air',null,null),(7,127,11002,'Bottes de Bowisse','Ces bottes exceptionnelles vous permettront de gagner un point de mouvement durant les combats.
INSERT INTO `equipements` (`id`,`id_item`, `id_image`, `nom`, `description`, `lvl`, `categorie`, `poids`, `liste_ingredient`, `metier`, `lvl_metier`, `dans_craft`, `drop_monstres`,`effets`, `critere`, `panoplie`) VALUES (51,703,16009,'Dora','Ce chapeau, en forme de corbeille à fruits en osier renversée, est en fait une corbeille à fruits renversée magique. Ce qui ne manquera pas d''impressionner votre entourage, surtout s''il est composé d''apprentis Bworks Mages.',36,'Chapeau',20,'1xOsier Enchanté + 1xLaine de Bouftou Royal + 10xFicelle en Lin + 2xBois de Charme + 2xBois de Châtaignier','Tailleur',36,null,null,'+11 à 60 Intelligence & +11 à 60 Force',null,null),(52,704,16010,'Dantgoule','Ce chapeau, confectionné à partir de Slips en cuir de Chafer Lancier tressés entre eux, vous rendra de toute évidence irrésistible auprès des femelles Bwork Mages, réputées pour leur féminité débordante, accentuée par leur barbe rousse.',36,'Chapeau',10,'1xCuir de Gargrouille + 1xCuir du Chef de Guerre Bouftou + 10xFicelle en Lin + 5xSlip en cuir du Chafer Lancier + 1xPotion Dantegoulesque','Tailleur',36,null,null,'+11 à 60 Agilité & +11 à 60 Vitalité',null,null),(53,705,16011,'Masque Tromatisant','Si vous voulez être traumatisé, il vous faut absolument cette magnifique noix de coco trouée. Nous rappelons à l''utilisateur que l''intérieur est équipé d''une paille et de boissons rafraîchissantes.',47,'Chapeau',10,'3xNoix de Grokoko Traumatisée + 4xNoix de Kokoko + 10xFicelle en Lin + 10xPoils de souris + 10xPoils du Sanglier','Tailleur',47,null,null,'+21 à 60 Force & +3 à 5 Dommages',null,null),(54,706,16012,'Crystal O''Boul','Hugne "le sans mémoire" possédait une magnifique boule de crystal que lui avait donné son pépé. Bien décidé à la conserver toute sa vie, il la plaça sur son chapeau, qu''il ne quittait jamais... Il est mort de fatigue, en essayant de la retrouver.',24,'Chapeau',12,'1xPierre de Crystaloboule + 10xPoils de souris + 10xPoils du Sanglier + 10xFicelle en Lin','Tailleur',24,null,null,'+21 à 50 Vitalité',null,'Panoplie O''Boul'),(55,707,16013,'Minotokorno','Ce chapeau en Corne de Minotoror est très efficace pour impressionner ses adversaires en se faisant passer pour un Minotoror. Cette ruse ne semble fonctionner que sur les adversaires simplets. Un Iop ou un Tofu devrait faire l''affaire.',33,'Chapeau',10,'1xPoils du Sanglier + 1xPoils de souris + 1xPoils du Minotoror + 1xFicelle en Lin + 2xCorne Brûlée du Minotoror','Tailleur',33,'Coiffe du Minotot',null,'+11 à 50 Force & +11 à 20 Puissance',null,'Panoplie du Minotoror'),(56,708,16014,'Houde','Ce chapeau en peau de larve verte magique donne à son porteur une allure guillerette, mais également un peu stupide. On ne peut pas tout avoir dans la vie, le porteur de ce chapeau en est d''ailleurs un excellent exemple.',30,'Chapeau',8,'1xPeau de Larve Verte Magique + 1xPlumes de Tofu + 10xFicelle en Lin + 10xPoils du Sanglier','Tailleur',30,null,null,'+11 à 50 Chance & +1 à 2% Critique',null,null),(57,709,16015,'Lorko Kasko','Ce chapeau en écorce enchantée appartenait à Lorko, un Bûcheron réputé qui n''a pas hésité à défier le célèbre Bûcheron Brouce Boulgoure, lors d''un fameux duel de déracinage d''Orme à mains nues dans la clairière de Liroye Merline.',37,'Chapeau',9,'2xAluminite + 1xSabot de Chef de Guerre Bouftou + 11xÉcorce d''Abraknyde + 10xLamelle de Champa Rouge + 1xCuir de Bouftou Royal','Tailleur',37,null,null,'+11 à 40 Force & +11 à 40 Intelligence & +11 à 40 Vitalité',null,null),(58,710,16016,'Korko Klako','Lo Korko Klako ost ouno chapo kil‍o bo. Tros ostotiko, tros bolo, il so porto par los plus bo dos Sadida.',17,'Chapeau',9,'4xTatouage de Mauvais Garçon + 1xSac à Patates Clair + 10xFicelle en Lin','Tailleur',17,null,null,'+1 Invocations & +16 à 25 Vitalité',null,null),(59,711,16017,'Jouik Krampe','Ce magnifique masque en cuir appartenait à Jouik Krampe, cousin de Zeurg. Tous deux étaient très portés sur les vêtements en cuir moulant. Un peu comme le Captain Chafer, la perversité en moins.',26,'Chapeau',15,'1xSac à Patates Sombre + 1xFicelle en Lin + 10xLaine de Boufton Noir + 10xPoils de Black Tiwabbit','Tailleur',26,'Korko Kousto',null,'+1 Invocations & +11 à 50 Intelligence','Être un Sadida ',null),(60,712,16018,'Gulliver','Ce chapeau confectionné à partir d''oreilles cousues entre elles, vous rendra tout simplement unique tant il est original. On raconte qu''il a jadis permis à Rakmar Bilgout de séduire la reine des Bouftous, pour ensuite l''égorger plus facilement.',32,'Chapeau',10,'20xOreille de Kolérat + 15xOreille du Grand Pa Wabbit + 30xOreilles du Tiwabbit + 10xOreille de Foufayteur + 25xOreilles de Croc Gland','Tailleur',32,null,null,'+16 à 25 Sagesse',null,null),(61,732,9030,'Alliance de Silimelle','Cette magnifique alliance a été volée à Silimelle la Brasseuse de Bière, lors d''une soirée un peu trop arrosée à la taverne. Voilà ce qui arrive lorsque l''on paie des coups à des inconnus...',26,'Anneau',1,'1xDiamant + 2xManganèse + 3xEtain + 5xArgent','Bijoutier',26,null,null,'+25 Sagesse & -5 Force & -5 Intelligence',null,null),(62,733,11007,'Bottes de Ragalde','Ragalde était sûrement un des Guerriers Iop les plus téméraires, ces bottes magiques lui ont permis d''occire bon nombre d''indésirables.',49,'Bottes',1,'1xCuir de Bouftou Royal + 1xCuir du Chef de Guerre Bouftou + 1xSaphir + 10xCuir de Gargrouille + 5xOr','Cordonnier',49,'Bottes du Rat Noir',null,'+11 à 20 Force & +11 à 20 Agilité & +11 à 20 Intelligence & +11 à 30 Chance & +2 à 5 Prospection & +21 à 30 Vitalité',null,null),(63,734,10007,'Ceinture de Sécurité','Cette ceinture permettait aux gardes du Château d''Amakna de contenir les émeutes et autres débordements lors des fêtes populaires.',17,'Ceinture',10,'5xPlanche Agglomérée + 15xCuir de Bouftou + 4xCrème à bronzer','Cordonnier',17,'Marteau Pospodrol, Petit Marteau Pospodrol, Grand Marteau Pospodrol, Puissant marteau Pospodrol',null,'+16 à 25 Force & +2 à 3 Fuite',null,null),(64,737,23002,'Dofus Émeraude','Plus vert qu''un Tofu malade, le Dofus Émeraude fut créé par Aerafal, le Dragon de l''Air. Pour créer quelque chose d''aussi vert, il devait avoir le mal de l''air. Un comble, non ?',6,'Dofus',5,null,null,0,null,null,'+200 Vitalité',null,null),(65,739,23003,'Dofus Turquoise','Le Dofus Turquoise fut créé par Aguabrial, le Dragon de l''Eau. Quand on y pense, c''est fou ce qu''un Dragon peut faire avec de l''eau...',6,'Dofus',5,null,null,0,null,null,'+10% Critique',null,null),(66,744,17001,'Cape de Bowisse','Cette cape est comme son porteur, unique en son genre et trop souvent sous-estimée.
INSERT INTO `equipements` (`id`,`id_item`, `id_image`, `nom`, `description`, `lvl`, `categorie`, `poids`, `liste_ingredient`, `metier`, `lvl_metier`, `dans_craft`, `drop_monstres`,`effets`, `critere`, `panoplie`) VALUES (101,862,11004,'Sandales d''Intelligence','Ces bottes permettent d''augmenter l''intelligence de leur porteur, tout en lui conférant un style vraiment à la mode par les temps qui courent grâce à leur semelle en Bois de Noyer qui offre une adhérence remarquable.',6,'Bottes',10,'5xRelique d''Incarnam + 5xEau Potable','Cordonnier',6,null,null,'+7 à 10 Intelligence & +1 Dommages Feu',null,null),(102,863,11005,'Bottes Agilesques','Ces bottes permettent d''augmenter l''agilité de leur porteur, afin qu''il puisse se prendre pour un Tofu plus facilement, lorsque l''envie lui prend de faire l''idiot.',6,'Bottes',10,'5xLaine Céleste + 5xQueue de Chapardam','Cordonnier',6,null,null,'+7 à 10 Agilité & +1 Dommages Air',null,null),(103,864,11001,'Bottes de Chance','Ces bottes augmentent la chance de leur porteur afin de l''aider à trouver des objets très puissants, comme de la bave de Bouftou par exemple.',7,'Bottes',10,'5xChampignon + 5xPlume Chimérique','Cordonnier',7,null,null,'+7 à 10 Chance & +1 Dommages Eau',null,null),(104,867,1009,'Dragolyre','Cette splendide amulette confectionnée à partir de restes de Dragoeuf, est suffisamment impressionnante pour faire battre en retrait la majorité des ennemis normalement constitués.',32,'Amulette',2,'2xAluminite + 1xSubstrat de Buisson + 9xCorne de Bouftou + 11xAmbre + 1xCuir de Bouftou Royal','Bijoutier',32,'Dragolyre Remaniée',null,'+1 PA & -35 Force & -35 Intelligence & +1 Invocations & -35 Chance & -35 Agilité',null,null),(105,888,11006,'Grandes Bottes de Concentration','Ces bottes sont si confortables qu''elles permettent au héros qui les chausse d''accroître sa concentration.',26,'Bottes',15,'11xOeil de Boufton + 11xCuir de Boufton Noir + 9xLaine de Boufton Blanc + 8xPoils du Sanglier','Cordonnier',26,null,null,'+2% Critique & +15 Intelligence',null,null),(106,889,11006,'Puissantes Bottes de Concentration','Ces bottes sont si confortables qu''elles permettent au héros qui les chausse d''accroître sa concentration.',27,'Bottes',15,'1xOeil de Boufton + 13xCuir de Boufton Noir + 10xLaine de Boufton Blanc + 8xPoils du Sanglier','Cordonnier',27,null,null,'+2% Critique & +20 Intelligence',null,null),(107,892,11003,'Bottes du Bouftou','Ces bottes sont confectionnées en cuir de Bouftou, elles permettent aux éleveurs de s''approcher plus facilement des Bouftous pour les tuer, grâce à l''odeur qu''elles dégagent.',4,'Bottes',10,'5xQueue de Chapardam + 5xBave de Bouftou','Cordonnier',4,null,null,'+5 à 7 Agilité & +2% Critique',null,null),(108,905,11009,'Bottes de Klime','La légende raconte que ces bottes ont été échangées contre d''autres bottes, défectueuses mais surpuissantes, qui avaient été fabriquées par un cordonnier maladroit manquant de sommeil.',41,'Bottes',10,'1xCuir de Bouftou Royal + 1xCuir du Chef de Guerre Bouftou + 1xSlip en cuir du Chafer Lancier + 15xLaine de Bouftou + 15xCuir de Gargrouille','Cordonnier',41,null,null,'+21 à 25 Vitalité & +21 à 25 Agilité & +21 à 30 Force',null,null),(109,906,11010,'Petites Bottes de Poursuite','Ces bottes permettaient à Hogmeiser de poursuivre les seuls êtres vivants qu''il ne supportait pas, les enfants. Heureusement pour eux, la lourdeur de ces bottes l''empêchait de leur botter le derrière.',31,'Bottes',9,'2xCuir du Chef de Guerre Bouftou + 1xPeau de Larve Champêtre + 1xÉtoffe de Dok Alako + 1xPeau de serpentin + 2xCuir du Cochon de Lait','Cordonnier',31,null,null,'+1 PM & +6 à 10 Chance & -1 PA',null,null),(110,907,11010,'Bottes de Poursuite','Ces bottes permettaient à Hogmeiser de poursuivre les seuls êtres vivants qu''il ne supportait pas, les enfants. Heureusement pour eux, la lourdeur de ces bottes l''empêchait de leur botter le derrière.',34,'Bottes',10,'1xPeau de Larve Champêtre + 1xPeau de serpentin + 1xÉtoffe de Dok Alako + 4xCuir du Chef de Guerre Bouftou + 4xCuir du Cochon de Lait','Cordonnier',34,null,null,'+1 PM & +16 à 20 Chance & -1 PA',null,null),(111,908,11010,'Grandes Bottes de Poursuite','Ces bottes permettaient à Hogmeiser de poursuivre les seuls êtres vivants qu''il ne supportait pas, les enfants. Heureusement pour eux, la lourdeur de ces bottes l''empêchait de leur botter le derrière.',37,'Bottes',11,'1xPeau de Larve Champêtre + 6xCuir du Chef de Guerre Bouftou + 6xCuir du Cochon de Lait + 1xPeau de serpentin + 1xÉtoffe de Dok Alako','Cordonnier',37,null,null,'+1 PM & +26 à 30 Chance & -1 PA',null,null),(112,909,11010,'Puissantes Bottes de Poursuite','Ces bottes permettaient à Hogmeiser de poursuivre les seuls êtres vivants qu''il ne supportait pas, les enfants. Heureusement pour eux, la lourdeur de ces bottes l''empêchait de leur botter le derrière.',39,'Bottes',12,'1xPeau de Larve Champêtre + 1xPeau de serpentin + 1xÉtoffe de Dok Alako + 8xCuir du Chef de Guerre Bouftou + 8xCuir du Cochon de Lait','Cordonnier',39,null,null,'+1 PM & +26 à 35 Chance & -1 PA',null,null),(113,910,11011,'Bottes d''Hogmeiser','Ces bottes ont appartenu à Hogmeiser pendant des années, avant qu''il ne les perdent lors d''un pari stupide avec Grizmine. La morale de l''histoire, c''est qu''il ne faut jamais jouer avec un Ecaflip.',75,'Bottes',20,'2xCuir de Bouftou Royal + 2xCuir du Cochon de Lait + 1xBottes Usées d''Hogmeiser + 3xLaine de Bouftou Royal + 3xLaine du Chef de Guerre Bouftou + 3xCuir du Chef de Guerre Bouftou + 3xCuir du Dragon Cochon','Cordonnier',75,'Bottes Dorées d''Hogmeiser',null,'+31 à 40 Sagesse & +31 à 40 Agilité & +5 à 6% Critique & +3 à 4 Dommages & +1 Invocations & +31 à 40 Vitalité',null,null),(114,911,11011,'Bottes Usées d''Hogmeiser','Ces bottes usées ont appartenu à Hogmeiser pendant des années, elles sont tellement usées, qu''elles sont surtout handicapantes. Un très bon Cordonnier devrait pouvoir les réparer avec de la chance.',1,'Bottes',10,null,null,0,'Bottes d''Hogmeiser','Bouflet le Puéril, Chef de Guerre Bouftou','-1 PM & -1 PA',null,null),(115,912,11012,'Bottes Dorées d''Hogmeiser','Ces magnifiques bottes d''Hogmeiser sont recouvertes d''une fine couche de pierres précieuses, pour les rendre insensibles à certaines attaques magiques. C''est avec ces bottes que Brouce Boulgoure a écrasé la tête du Roi des Abraknydes il y a très longtemps, pour animer l''ouverture de la foire du Trool.',95,'Bottes',30,'1xBottes d''Hogmeiser + 1xCuir de Bouftou Royal + 1xDiamant + 1xSaphir + 1xCuir du Dragon Cochon + 10xOr + 3xCuir du Chef de Guerre Bouftou','Cordonnier',95,null,null,'+31 à 40 Sagesse & +36 à 50 Agilité & +7 à 8% Critique & +5 à 6 Dommages & +51 à 70 Vitalité & +10 à 12 Résistance Eau & +10 à 12 Résistance Air & +10 à 12 Résistance Feu & +1 à 2 Invocations',null,null),(116,916,1009,'Amulette de Silicate','Silicate a confectionné cette Amulette un matin d''hiver, pour un célèbre aventurier du nom de Rimb Vardi, qui pourchassait un vieux Bwork Mage, responsable de plusieurs catastrophes naturelles déclenchées par sa magie trop approximative.',37,'Amulette',2,'2xAluminite + 1xSubstrat de Buisson + 9xAmbre + 11xLamelle de Champa Vert + 1xLaine de Bouftou Royal','Bijoutier',37,null,null,'+10% Critique & +1 Résistance Eau & +1 Résistance Air & +1 Résistance Feu & +1 Résistance Terre & +1 Résistance Neutre & -30 Agilité & -20 Vitalité',null,null),(117,917,1064,'La Feuille d''Automne','Cette Broche a été confectionnée un matin d''automne au milieu d''une forêt de Hêtres et d''Ormes non loin de l''île d''Olag-lacustre. Elle est considérée par nombre de bûcherons comme une véritable relique.',89,'Amulette',2,'1xBois Ancestral + 10xOr + 10xBois de Charme + 2xSaphir + 10xBois d''Ebène + 10xBois de Merisier + 10xBois d''Orme','Bijoutier',89,'La Feuille de Printemps',null,'+1 à 2 Portée & +6 à 10% Critique & +21 à 50 Chance & +21 à 50 Agilité & +11 à 15 Prospection & -50 Force & -50 Intelligence',null,null),(118,924,11009,'Puissantes Bottes de Klime','L''agilité que confèrent ces bottes permettait à Klime le cordonnier de monter facilement sur le dos de n''importe quel Bouftou. Il est mort en confondant un Bouftou avec une Gelée, après une soirée trop arrosée passée à la taverne.',12,'Bottes',10,'5xRelique d''Incarnam + 5xPlume Chimérique + 5xEau Potable','Cordonnier',12,null,null,'+21 à 25 Vitalité & +21 à 25 Agilité & -1 à 10 Force',null,null),(119,934,17189,'Cape de Rougeur','Cette cape augmente la vie de son porteur.',16,'Cape',5,'5xPétale de Rose Démoniaque + 5xPaupière d''Étoile + 5xGroin de Sanglier','Tailleur',16,null,null,'+21 à 30 Vitalité & +2 à 3 Fuite',null,null),(120,937,17192,'Cape Bleutée','Cette cape augmente la chance de son porteur.',7,'Cape',10,'5xOeuf de Tofu + 5xFeu Intérieur','Tailleur',7,null,null,'+11 à 15 Chance & +1 Dommages Eau',null,null),(121,940,16020,'Louffeur','Le Louffeur prévient des dégâts physiques.',1,'Chapeau',1,'5xOrtie + 5xCendres Éternelles','Tailleur',1,'Petite Masse Tmosfer, Masse Tmosfer, Longue Masse Tmosfer, Redoutable Masse Tmosfer',null,'+4 à 5 Résistance Terre & +4 à 5 Résistance Neutre',null,null),(122,941,16022,'Bandeau Komintot','Le Komintot permet d''augmenter la sagesse de son porteur.',3,'Chapeau',10,'2xPoils de souris + 5xPétale Diaphane','Tailleur',3,null,null,'+5 à 7 Sagesse',null,null),(123,942,16023,'Lisseur','Le Lisseur prévient des dégâts magiques.',2,'Chapeau',10,'5xPlume Chimérique + 5xVolve d''Aminite','Tailleur',2,'Petite Baguette Meneuse, Baguette Meneuse, Grande Baguette Meneuse, Excellente Baguette Meneuse',null,'+4 à 5 Résistance Eau & +4 à 5 Résistance Air & +4 à 5 Résistance Feu',null,null),(124,947,17194,'Cape Sombre','Cette cape augmente la sagesse de son porteur.',18,'Cape',10,'15xPatte d''Arakne + 15xLaine de Boufton Noir + 5xCrème à bronzer','Tailleur',18,null,null,'+7 à 10 Sagesse',null,null),(125,948,17016,'Abracape','Cette magnifique cape est un brillant mélange entre l''essence d''Abraknyde e